console.log(this.action);
const roc=API.cache('roc');

const spectraDataSet=API.cache('spectraDataSet');

switch (this.action.name) {
    default:
        await spectraDataSet.processAction(this.action);
}

filterRow:
if (this.event==='rowsChanged') {
    let targetID=String(API.getData('preferences').targetID);
    for (var i=0; i<this.rows.length; i++) {
        var row=this.rows[i];
        row.item.bgcolor = String(row.item.id) === targetID ? '#98F274' : undefined;
    }
