script:
console.log('Load and process', this.action, this.variable);

const recreateCharts = await API.require('vh/eln/sample/ml/recreateCharts');

recreateCharts(this.variable);

if (! this.button) this.button='default';

var layers={
    'default': {
        layer:'Default layer'
    },
    'similarity': {
        layer:'Similarity',
        callback: () => {
            API.doAction('RecalculateTree',API.getData('preferences'));
        }
    },
}

if (layers[this.button]) {
    for (var key of Object.keys(layers)) {
        this.enableButton(key);
    }
    API.switchToLayer(layers[this.button].layer);
    if (layers[this.button].callback) {
        layers[this.button].callback();
    }
    this.disableButton(this.button);
}

if (this.button) { // just used for debug
    this.action={
        name:this.button
    }
}

const UI=await API.require('src/util/ui');
let currentAnalysisResults=API.getData('currentAnalysisResults');

let userAnalysisResults=API.cache("userAnalysisResults");

switch (this.action.name) {
    case 'saveResultTemplate': {
        let value = (currentAnalysisResults) ? currentAnalysisResults.resurrect().$id[3] : '';
        let name=await UI.enterValue({
            label: 'Enter the preference name',
            buttonLabel: 'Save',
            value
        });
        if (!name) return;
        let result = await userAnalysisResults.saveTemplate(name, API.getData('preferences').resurrect(), getData());
        API.doAction('refreshResults');
        break;
    }
    case 'loadExample': {
        let cdb = new CDB();
        let data = await cdb.get('upload/example.json');
        setData(data);
        break;
    }
    case 'refreshResults':
        console.log('refresh');
        await userAnalysisResults.refresh();
        break;
    case 'saveAsAttachment': {
        let json = JSON.stringify(getData());
        console.log(json);
        let cdb = new CDB();
        let data = new Blob([json], {type: 'application/json'});
        cdb.upload({
            data,
            name: 'upload/example.json'
        });
    }
    case 'deleteResultTemplate': {
        let result=await UI.confirm('Are you sure you want to delete this template ?', 'Delete', 'Cancel', {});
        if (! result) return;
        await userAnalysisResults.delete(this.action.value);
        API.doAction('refreshResults');
        break;
    }
    case 'loadResultTemplate': {
        let result = await userAnalysisResults.loadResult(this.action.value);
        setData(result);

        break;
    }
    case 'selectResultTemplate':
        let templates=API.getData('analysisTemplates');
        if (! templates) {
            UI.showNotification('Only available when linked to a database','warning');
            return;
        }
        let result = await UI.form(`
            <div style="zoom: 1.5;">
                <form>
                    <p style="padding-bottom: 10px">
                    Reload one of the previously saved preferences:
                    </p>
                    <select name='template'>
                        ${templates.map( template => {
                            let key=String(template.$id[3]);
                            let _id=String(template._id);
                            return `
                                <option value="${_id}">${key}</option>
                            `
                        })}

                    </select>
                    <input type="submit" value="Load preferences"/>
                </form>
            </div>
        `)

        if (result.template) {
            API.doAction('loadResultTemplate', {_id:result.template})
        }

        break;
}

function setData(result) {
        API.createData('description', result.description);
        API.createData('preferences', result.preferences);
}

function getData() {
    return {
        preferences: API.getData('preferences').resurrect(),
        description: API.getData('description').resurrect(),
    }
}

let value=this.action.value;

switch (this.action.name) {
    case 'trackClicked': {
        if (value.event.altKey && value.event.shiftKey) {
            let key=Object.keys(value.data)[0];
            let index=value.data[key].indexClosest;
            let preferences=API.getData('preferences');
            preferences.setChildSync(['display','autocorrelationIndex'],Number(index));
        }
        break;
    }
    case 'trackMove': {
        let displayPreferences = API.getData('preferences').display.resurrect();
        const trackMove = this.action.value.data.chart;
        if (! trackMove || ! displayPreferences.trackingInfo || displayPreferences.original==='true') {
            API.createData('trackAnnotations', []);
            return;
        }
        const spectraProcessor=API.cache('spectraProcessor');
        const annotations=spectraProcessor.getTrackAnnotation(trackMove.indexBefore)
        API.createData('trackAnnotations', annotations)
        break;
    }
    case 'SetTarget':
        const preferences=API.getData('preferences');
        preferences.setChildSync(['targetID'],this.action.value.id)
        API.doAction('RecalculateSimilarity');
        break;
    case 'downloadNormalizedMatrix':
        const spectraProcessor = API.cache('spectraProcessor');
        let tsv=spectraProcessor.getNormalizedTSV();
        let fileSaver=await API.require('file-saver');
        var blob = new Blob([tsv], {type: "text/tab-separated-values;charset=utf-8"});
        fileSaver( blob, "data.tsv");
        break;
    case 'showSimilarityAlgorithm':
        showSimilarityAlgorithm();
        break;
    default:
}

console.log(this.action);

if (this.button) {
    this.action={name:this.button};
}



const spectraProcessor = API.cache('spectraProcessor');
const spectra=spectraProcessor.spectra;
switch(this.action.name) {
    case 'RecalculateSimilarity':
        updateSimilarity();
        break;
    case 'RecalculateTree':
        updateSimilarity();
        calculateTree(this.action.value.resurrect());
        break;
}

function updateSimilarity() {
    console.log('updateSimilarity');
    const spectraInDataset = API.getData('spectraInDataset');

    const preferences=API.getData('preferences').resurrect();
    let targetSpectrum = spectraProcessor.getSpectrum(preferences.targetID);
    if (! targetSpectrum) return;
    for (let selectedSpectrum of spectraInDataset) {
        const spectrum=spectraProcessor.getSpectrum(String(selectedSpectrum.id));
        selectedSpectrum.distance=ML.Distance[preferences.distance.algorithm](spectrum.normalized.y, targetSpectrum.normalized.y);
    }
    spectraInDataset.sort( (a,b) => a.distance-b.distance);
    spectraInDataset.triggerChange();
    window.setTimeout( () => API.doAction('ScrollToRow', 0), 0);
}

function calculateTree(preferences) {
    API.createData('subtree',{});
    const similarityMatrix = new ML.Matrix(spectra.length, spectra.length);
    for (let i=0; i<spectra.length; i++) {
        const spectrum1=spectra[i];
        for (let j=i; j<spectra.length; j++) {
            const spectrum2=spectra[j];
            const similarity=ML.Distance[preferences.distance.algorithm](spectrum1.normalized.y, spectrum2.normalized.y);
            if (isNaN(similarity)) {
                UI.showNotification('Can not calculate tree using '+preferences.distance.algorithm+', distance contains NaN', 'error');
                return;
            }
            similarityMatrix.set(i,j,similarity);
            if (i !== j) {
                similarityMatrix.set(j,i,similarity);
            }
        }
    }

    console.log(similarityMatrix);
   // return;

    const { agnes } = ML.HClust;

    const tree = agnes(similarityMatrix.to2DArray(), {
      method: preferences.clustering.algorithm,
    });

    let maxHeight=0;
    tree.traverse ( node => {
        if (node.height>maxHeight) maxHeight=node.height;
    });

    tree.traverse ( node => {
        node.distance=Math.round(node.height / maxHeight * 10000)/10000;
        if (node.isLeaf) {
            node.data={
                $color: spectra[node.index].meta.color,
                _highlight: spectra[node.index].id,
                toc: spectra[node.index].meta.info
            }
        }
    })

    API.createData('tree', tree);
}

const [
    Roc,
    loadTwig,
    UserAnalysisResults,
    Toc,
    SpectraDataSet,
    libs,
] = await API.require([
    'vh/rest-on-couch/Roc',
    'vh/eln/templates/loadTwig',
    'vh/rest-on-couch/UserAnalysisResults',
    'vh/eln/Toc',
    'vh/eln/sample/ml/SpectraDataSet',
    'vh/eln/libs',
]);

const SpectraProcessor = (await API.require('SpectraProcessor')).SpectraProcessor;
API.cache('spectraProcessor', new SpectraProcessor({}));

if((typeof IframeBridge)!=='undefined') {
    IframeBridge.onMessage(onMessage);
    IframeBridge.ready();
}

async function onMessage(data) {
    if(data.type === 'tab.data') {
        var couchDB = data.message.couchDB;
        var uuid = data.message.uuid;
        API.cache('couchDB', couchDB);
        API.cache('uuid', uuid);
        var roc = new Roc(couchDB);
        API.cache('roc', roc);

        var userAnalysisResults = new UserAnalysisResults(roc, data.message.uuid);
        API.cache("userAnalysisResults", userAnalysisResults);
        userAnalysisResults.refresh();
        API.createData('description', '');



        const sampleToc = new Toc(roc);
        sampleToc.initializeGroupForm();
        API.cache('sampleToc', sampleToc);

        const spectraDataSet = new SpectraDataSet(roc, sampleToc, {
            defaultAttributes: {}
        });
        spectraDataSet.initializeAnalysis();
        API.cache('spectraDataSet', spectraDataSet);

    }
}

API.createData('spectraInDataset',[]);

API.createData('preferences', {
    data: {keep: true},
    display: {
        selection: 'selected',
        original: false,
        trackingInfo: false,
        boxplot: false,
        boxplotOptions: {
            q13FillColor : '#000',
            q13FillOpacity : 0.3,
            q2StrokeColor : '#000',
            q2StrokeWidth : 3,
            minMaxFillColor : '#000',
            minMaxFillOpacity : 0.15
        },
        autorefresh: true
    },
    normalization: {numberOfPoints: 1024},
    distance: {
        algorithm: 'jaccard'
    },
    clustering: {
        algorithm: 'ward'
    }
});
API.createData('description', '');
API.createData('code', '');
API.createData('codeReport', '');
API.createData('reportTemplate', '');

loadTwig(['org.cheminfo.sampleToc.chemical.default']);

API.require('vh/util/tips').then( Tips => {
    Tips.showTips();
    Tips.addPageHelp({iconSize:'fa-4x'});
});
