script:
console.log(this.action);
const roc=API.cache('roc');

const spectraDataSet=API.cache('spectraDataSet');

switch (this.action.name) {
    case 'pcaaddSpectraToSelection': {
        let scatterSelected=API.getData('scatterSelected').map( entry => String(entry.id));
        let spectraInDataset=API.getData('spectraInDataset').filter( entry => scatterSelected.includes(String(entry.id)));
        API.createData('currentlySelectedSpectra', spectraInDataset);
        window.setTimeout( () => {
            API.doAction('addSpectraToSelection');
        });
        break;
    }
    case 'pcaselectCurrentSpectraSelection': {
        let scatterSelected=API.getData('scatterSelected').map( entry => String(entry.id));
        let spectraInDataset=API.getData('spectraInDataset').filter( entry => scatterSelected.includes(String(entry.id)));
        API.createData('currentlySelectedSpectra', spectraInDataset);
        window.setTimeout( () => {
            API.doAction('selectCurrentSpectraSelection');
        });
        break;
    }
    case 'pcaHideSpectra': {
        let scatterSelected=API.getData('scatterSelected').map( entry => String(entry.id));
        let spectraInDataset=API.getData('spectraInDataset').filter( entry => scatterSelected.includes(String(entry.id)));
        API.createData('currentlySelectedSpectra', spectraInDataset);
        window.setTimeout( () => {
            API.doAction('hideSpectra');
        });
        break;
    }
    case 'clickedPCAShape':
        if (this.action.value.event.altKey) {
            // we remove the element from the selection
            let spectraInDataset = API.getData('spectraInDataset');
            let index=spectraInDataset.indexOf(this.action.value.data.info);
            spectraInDataset.splice(index,1);
            spectraInDataset.triggerChange();
        }
        break;
    case 'deletePCASelection': {

            let spectraInDataset = API.getData('spectraInDataset');
            let scatterSelected=API.getData('scatterSelected');

            for (let selected of scatterSelected) {
                for (let i=0; i<spectraInDataset.length; i++) {
                    let spectrum=spectraInDataset[i];
                    if (String(selected.id)===String(spectrum.id)) {
                        spectraInDataset.splice(i,1);
                        break;
                    }
                }
            }
            spectraInDataset.triggerChange();
        }
        break;
    default:
        await spectraDataSet.processAction(this.action);
}

const normalized = API.cache('spectraProcessor').getNormalizedData();


console.log('Calculate PCA');

let spectraInDataset=API.getData('spectraInDataset');

spectraInDataset.forEach( (entry) => entry.pcaModel=true);

let data=normalized.matrix;
let PCA = ML.PCA;


console.time('PCA '+data.length+'x'+data[0].length);

let pca;

// if we use method: 'covarianceMatrix' it is 100 times slower !
// SVD is the default method

try {
    if (true) { // Nipals is slower for 500 x 2000
        pca = new PCA(data, {scale: true});
    } else {
        pca = new PCA(data, {scale: true, method:'NIPALS', nCompNIPALS:10});
    }
} catch (error) {
    UI.showNotification(error, 'error');
    return;
}

console.timeEnd('PCA '+data.length+'x'+data[0].length);

API.cache('pca', pca);

spectraInDataset.triggerChange();

if (! this.button) {
    if((typeof IframeBridge)!=='undefined') {
        this.showButton('default');
        this.showButton('pca');
        this.disableButton('default');
        this.button='default';
    } else {
        this.showButton('pcaOffline');
        this.button='pcaOffline';
        this.disableButton('pcaOffline');
    }
    return;
}

console.log('Continue init');

var layers={
    'default': {
        layer:'Default layer'
    },
    'pca': {
        layer: 'PCA',
        callback: () => {
            // we don't calculate automatically because we don't ALWAYS want calculations
            // API.doAction('calculatePCA', '');
        }
    },
    'pcaOffline': {
        layer: 'PCA Offline'
    },
    'pcapc': {
        layer: 'PCA PC',
        callback: () => {
            API.doAction('calculatePCADerivedCharts');
            API.getData('pcaGridTemplate').triggerChange();
        }
    }
}

if (layers[this.button]) {
    for (var key of Object.keys(layers)) {
       this.enableButton(key);
    }
    API.switchToLayer(layers[this.button].layer);
    if (layers[this.button].callback) {
        layers[this.button].callback();
    }
    this.disableButton(this.button);
}

console.log('Generate PCA annotations', this.action);
if (! API.getData('preferences')) return;
let preferences=API.getData('preferences').resurrect();

if (this.action && this.action.name==='pcaPreferencesChanged') { // preferences changed
    preferences=this.action.value.resurrect();
    console.log(preferences)
    let hash=preferences.pca.x+'-'+preferences.pca.y;
    if (API.cache('previousPCAprefs')===hash) return;
    API.cache('previousPCAprefs',hash);
}

let pca = API.cache('pca');
if (! pca) return;

console.log('a')
const normalized = API.cache('spectraProcessor').getNormalizedData();
console.log('b')
if (! normalized) return;
let spectraInDataset=API.getData('spectraInDataset');
let data=normalized.matrix;

let selectedInfo={
    nbSelected: spectraInDataset.length,
    nbDisplay: spectraInDataset.reduce( (previous, entry) => previous+entry.display, 0 ),
    nbPCA: spectraInDataset.reduce( (previous, entry) => previous+entry.pcaModel, 0 ),
}
API.createData('selectedInfo',selectedInfo);
console.log('c')
let projection = pca.predict(data);

let x = projection.getColumn(preferences.pca.x);
let y = projection.getColumn(preferences.pca.y);
console.log('d')
let pcaAnnotations=[];
for (let i=0; i<normalized.ids.length; i++) {
    let currentID=normalized.ids[i];
    let spectrum = spectraInDataset.filter( spectrum => String(spectrum.id)===currentID)[0];
    spectrum.pca={
        x:x[i],
        y:y[i]
    }
    pcaAnnotations.push({
        _highlight: [spectrum._highlight],
        "type": "ellipse",
        "position": [
          {
            "x": x[i],
            "y": y[i]
          }
        ],
        "props": {
          "rx": "6px",
          "ry": "6px"
        },
        "strokeWidth": "0px",
        "fillColor": "rgba(0,0,0,0)",
        "info": spectrum
    })
}

API.createData('pcaAnnotations', pcaAnnotations);

var selected = normalized.meta.map(
    entry => {
         return {
            shape: "circle",
            stroke: entry.color,
            r: 5
        }
    }
)

var unselected = normalized.meta.map(
    (entry,index) => {

        let spectrum = spectraInDataset.filter( spectrum => String(spectrum.id)===normalized.ids[index])[0];

        if (DataObject.resurrect(spectrum.pcaModel)) {
            return {
                shape: "circle",
                fill: entry.color,
                r: 3
            }
        }
        return {
            shape: "circle",
            stroke: entry.color,
            fill: 'white',
            r: 3
        }
    }
)



let styles={
        unselected,
        selected: selected
}

API.createData('pca', {x, y, styles, info: normalized.ids.map( (id,index) =>
    {
        return {
            id,
            meta: normalized.meta[index]
        }
    }), type: 'scatter'});

if (this.variable==='preferences') return;

var explainedVariance = pca.getExplainedVariance();

let pcaGrid=[];

let rendererStyle={
    marker: {
        default: {},
        modifiers: unselected
    }
}


for (let i=0; i<4; i++) {
    pcaGrid[i]=[];
    for (let j=0; j<4; j++) {
        let x = projection.getColumn(i);
        let y = projection.getColumn(j);
        pcaGrid[i][j]={
            title: 'PCA PC '+(i+1)+' vs '+(j+1)+
                ' ('+Number((explainedVariance[i]+explainedVariance[j])*100).toFixed(2)+'%)',
            "axes": {
                "x": {
                  "label": "PC "+(i+1)+' ('+Number(explainedVariance[i]*100).toFixed(2)+'%)'
                },
                "y": {
                  "label": "PC "+(j+1)+' ('+Number(explainedVariance[j]*100).toFixed(2)+'%)'
                }
            },
            series: [
                {
                    type: 'scatter',
                    data: {
                        x,
                        y
                    },
                    style:rendererStyle
                }
            ]
        };
    }
}

API.createData('pcaGrid', pcaGrid );

// may check: https://www.benjaminbell.co.uk/2018/02/principal-components-analysis-pca-in-r.html

let preferences=API.getData('preferences').resurrect();
if (! preferences.pca.ellipse) {
    API.createData('ellipsis', []);
    return;
}

let spectraInDataset = API.getData('spectraInDataset');

let categories={};

for (let spectrum of spectraInDataset) {
    let category=String(spectrum.category);
    if (! spectrum.pca) continue;
    if (! categories[category]) {
         categories[category]={
             category: category,
             color: spectrum.color,
             data: []
         }
    }
    categories[category].data.push(spectrum.pca);
}
let categoriesArray=Object.keys(categories).map( key => categories[key]);
categoriesArray=categoriesArray.filter( (entry) => entry.data.length>2);

let annotations=[];
console.log(preferences.pca.sd)
for (let category of categoriesArray) {
    const x=category.data.map( datum => datum.x);
    const y=category.data.map( datum => datum.y);
    let annotation=Ellipse.getEllipse({x,y}, {
        nbSD:Number(preferences.pca.sd),
        props: {
            fillOpacity: 0.2,
            fillColor: String(category.color),
            label: {

            }
        }
    });
    if (preferences.pca.ellipseCategory) {
        annotation.label=[
          {
            "text": String(category.category),
            "size": 14,
            "anchor": "right",
            "color": String(category.color),
            "position": {
              "x": annotation.properties.position[0].x,
              "y": annotation.properties.position[0].y
            }
          }
        ];
    }
    annotations.push(annotation);
}

window.setTimeout( () => {  // need to force the redraw hover
   API.createData('ellipsis', annotations);
   API.getData('pcaAnnotations').triggerChange();
})

var PCA = ML.PCA;

if (this.button) { // just used for debug
    this.action={
        name:this.button
    }
}

const UI=await API.require('src/util/ui');
let currentAnalysisResults=API.getData('currentAnalysisResults');

let userAnalysisResults=API.cache("userAnalysisResults");

switch (this.action.name) {
    case 'saveResultTemplate': {
        if (! API.getData('analysisTemplates')) {
            UI.showNotification('Only available when linked to a database','warning');
            return;
        }
        let value = (currentAnalysisResults) ? currentAnalysisResults.resurrect().$id[3] : '';
        let name=await UI.enterValue({
            label: 'Enter the preference name',
            buttonLabel: 'Save',
            value
        });
        if (!name) return;

        let pca=API.cache('pca');
        let pcaModel=pca ? pca.toJSON() : undefined;

        let result = await userAnalysisResults.saveTemplate(name, API.getData('preferences').resurrect(), {
            preferences: API.getData('preferences').resurrect(),
            description: API.getData('description').resurrect(),
            spectraInDataset: API.getData('spectraInDataset').resurrect(),
            pcaModel
        });
        API.doAction('refreshResults');
        break;
    }
    case 'saveResult': {
        let value = (currentAnalysisResults) ? currentAnalysisResults.resurrect().$id[3] : '';
        let name=await UI.enterValue({
            label: 'Enter the name',
            buttonLabel: 'Save',
            value
        });
        if (!name) return;
        await userAnalysisResults.save(name, API.getData('preferences').resurrect(), {
            preferences: API.getData('preferences').resurrect(),
            fragments: API.getData('fragments').resurrect(),
            description: API.getData('description').resurrect()
        });
        API.doAction('refreshResults');
        break;
    }
    case 'deleteResultTemplate':
    case 'deleteResult': {
        let result=await UI.confirm('Are you sure you want to delete this template ?', 'Delete', 'Cancel', {});
        if (! result) return;
        await userAnalysisResults.delete(this.action.value);
        API.doAction('refreshResults');
        break;
    }
    case 'loadResultTemplate': {
        API.createData('pca', undefined);
        API.createData('pcaAnnotations', []);
        API.createData('ellipsis', []);

        let result = await userAnalysisResults.loadResult(this.action.value);

        let newPreferences=result.preferences;
        let preferences=API.getData('preferences');
        copyPreferences(newPreferences, preferences);
        preferences.triggerChange();
        API.getData('twigPreferences').triggerChange();
        let newPCA=PCA.load(result.pcaModel);
        API.cache('pca', newPCA);
        API.createData('description', result.description);
        API.createData('spectraInDataset', result.spectraInDataset);
        break;
    }
    case 'loadResult': {
        let result = await userAnalysisResults.loadResult(this.action.value);
        API.createData('description', result.description);
        let preferences=API.getData('preferences');
        copyPreferences(result.preferences, preferences);
        preferences.triggerChange();
        API.createData('fragments', result.fragments);
        break;
    }
    case 'refreshResults':
        await userAnalysisResults.refresh();
        break;
    case 'selectResultTemplate':
        let templates=API.getData('analysisTemplates');
        if (! templates) {
            UI.showNotification('Only available when linked to a database','warning');
            return;
        }
        let result = await UI.form(`
            <div style="zoom: 1.5;">
                <form>
                    <p style="padding-bottom: 10px">
                    Reload one of the previously saved preferences:
                    </p>
                    <select name='template'>
                        ${templates.map( template => {
                            let key=String(template.$id[3]);
                            let _id=String(template._id);
                            return `
                                <option value="${_id}">${key}</option>
                            `
                        })}

                    </select>
                    <input type="submit" value="Load preferences"/>
                </form>
            </div>
        `)

        if (result.template) {
            API.doAction('loadResultTemplate', {_id:result.template})
        }

        break;
}

function copyPreferences(newPreferences, preferences) {
    Object.keys(newPreferences).forEach( key => {
        preferences[key]=JSON.parse(JSON.stringify(newPreferences[key]))
    })
}

console.log('Create PCA related charts');
let pca=API.cache('pca');

const normalized = API.cache('spectraProcessor').getNormalizedData();

let data=normalized.matrix;
var explainedVariance = pca.getExplainedVariance().slice(0,10);
API.createData('explainedVariance',{
    x: Array(10).fill().map((item, index) => index+1), 
    y:explainedVariance}
);

var cumulativeVariance = {
    x: Array(10).fill().map((item, index) => index+1), 
    y: [0].concat(pca.getCumulativeVariance().slice(0,10))
};

API.createData('cumulativeVariance',cumulativeVariance);
API.createData('CumulativeVariance10', cumulativeVariance.y[10])

var loadings = pca.getLoadings();
const normalizedX=JSON.parse(JSON.stringify(normalized.x)); // performance issue if not there

API.createData('pc1', {x: normalizedX, y: loadings.getRow(0)});
API.createData('pc2', {x: normalizedX, y: loadings.getRow(1)});
API.createData('pc3', {x: normalizedX, y: loadings.getRow(2)});
API.createData('pc4', {x: normalizedX, y: loadings.getRow(3)});



var stdevs = pca.stdevs;

var min = ML.Array.min(stdevs);
var max = ML.Array.max(stdevs);
var scale = Chroma.scale(['blue', 'red']).domain([min, max]).mode('hsl');

var color = {
    x: normalizedX,
    y: JSON.parse(JSON.stringify(data[0])), // performance issues
    color: stdevs.map(val => scale(val).hex())
};

API.createData('color', color);

// just to spy the variable that are being updated

if (this.variable) {
    console.log('Update variable',this.variable);
}
if (this.action) {
    console.log('Update action',this.action.name);
}

console.log('Load and process', this.action, this.variable);

const recreateCharts = await API.require('vh/eln/sample/ml/recreateCharts');

await recreateCharts(this.variable);

API.doAction('changedSelectedSpectra');

let value=this.action.value;

switch (this.action.name) {
    case 'trackClicked': {
        if (value.event.altKey && value.event.shiftKey) {
            let key=Object.keys(value.data)[0];
            let index=value.data[key].indexClosest;
            let preferences=API.getData('preferences');
            preferences.setChildSync(['display','correlationIndex'],Number(index));
        }
        break;
    }
    case 'trackMove': {
        let displayPreferences = API.getData('preferences').display.resurrect();
        const trackMove = this.action.value.data.chart;
        if (! trackMove || ! displayPreferences.trackingInfo || displayPreferences.original==='true') {
            API.createData('trackAnnotations', []);
            return;
        }
        const spectraProcessor=API.cache('spectraProcessor');
        const annotations=spectraProcessor.getTrackAnnotation(trackMove.indexBefore)
        API.createData('trackAnnotations', annotations)
        break;
    }
    case 'downloadNormalizedMatrix':
        const spectraProcessor = API.cache('spectraProcessor');
        let tsv=spectraProcessor.getNormalizedText();
        let fileSaver=await API.require('file-saver');
        var blob = new Blob([tsv], {type: "text/tab-separated-values;charset=utf-8"});
        fileSaver( blob, "data.tsv");
        break;
    default:
}

let droppedText=API.getData('droppedText').resurrect();

const spectraProcessor = SpectraProcessor.SpectraProcessor.fromNormalizedMatrix(droppedText);

console.log(spectraProcessor.getNormalization())
console.log(spectraProcessor.getMemoryInfo());

const preferences=API.getData('preferences');
preferences.normalization=spectraProcessor.getNormalization();
preferences.triggerChange();

let spectraInDataset=[];
for (let spectrum of spectraProcessor.spectra) {
    console.log(spectrum.id)
    spectraInDataset.push( {
        id: spectrum.id,
        display: true,
        pcaModel: false,
        category: spectrum.meta.category,
        color: spectrum.meta.color,
        toc: {
            value: {
                reference: spectrum.id,
                misc: {
                    id: spectrum.id,
                    ...spectrum.meta
                }
            }
        },
        _highlight: Math.random()
    })
}

API.createData('memoryInfo', spectraProcessor.getMemoryInfo());

API.createData('spectraInDataset', spectraInDataset);


API.cache('spectraProcessor', spectraProcessor);

