API.createData('twigPreferences', twigPreferences);

var url="https://docs.google.com/document/d/1F206NUqZTC_vRA82QPfZNsHjHgMfxwe5yoCHjGm8txo/edit?usp=sharing";

require(['src/util/ui'], function(UI) {
    UI.dialog(
        '<iframe width="100%" height="100%" src="'+url+'"></iframe>',
        {width:1000, height: 800}
    );
})

if((typeof IframeBridge)==='undefined') {
    let Grid=await require('src/main/grid');
    Grid.setInitialLayerName('PCA Offline');
}


const [
    Roc,
    loadTwig,
    UserAnalysisResults,
    Toc,
    SpectraDataSet,
    libs,
    RangesManager,
] = await API.require([
    'vh/rest-on-couch/Roc',
    'vh/eln/templates/loadTwig',
    'vh/rest-on-couch/UserAnalysisResults',
    'vh/eln/Toc',
    'vh/eln/sample/ml/SpectraDataSet',
    'vh/eln/libs',
    'vh/eln/sample/RangesManager',
]);

const SpectraProcessor = (await API.require('SpectraProcessor')).SpectraProcessor;
API.cache('spectraProcessor', new SpectraProcessor({}));

if((typeof IframeBridge)!=='undefined') {
    IframeBridge.onMessage(onMessage);
    IframeBridge.ready();
} else {
    const spectraDataSet = new SpectraDataSet(undefined, undefined, {
        defaultAttributes: {
            pcaModel: false
        }
    });
    spectraDataSet.initializeAnalysis();
    API.cache('spectraDataSet', spectraDataSet);
}

async function onMessage(data) {
    if(data.type === 'tab.data') {
        var couchDB = data.message.couchDB;
        var uuid = data.message.uuid;
        API.cache('couchDB', couchDB);
        API.cache('uuid', uuid);
        var roc = new Roc(couchDB);
        API.cache('roc', roc);

        var userAnalysisResults = new UserAnalysisResults(roc, data.message.uuid);
        API.cache("userAnalysisResults", userAnalysisResults);
        userAnalysisResults.refresh();
        API.createData('description', '');

        const sampleToc = new Toc(roc);
        sampleToc.initializeGroupForm();
        API.cache('sampleToc', sampleToc);

        const spectraDataSet = new SpectraDataSet(roc, sampleToc, {
            defaultAttributes: {
                pcaModel: false
            }
        });
        spectraDataSet.initializeAnalysis({autoRefresh:false});
        API.cache('spectraDataSet', spectraDataSet);
    }
}

API.createData('spectraInDataset',[]);

const ranges = await API.createData('ranges', []);
const rangesManager = new RangesManager(ranges, {ensureLabel: true});
API.cache('rangesManager', rangesManager);


