            q2StrokeWidth : 3,
            minMaxFillColor : '#000',
            minMaxFillOpacity : 0.15
        },
        autorefresh: true
    },
    normalization: {numberOfPoints: 1024},
    pca: {
        x:0,
        y:1,
        ellipse:true,
        ellipseCategory:false,
        sd:2,
    }
});

